class Solution {
    public boolean isSubsequence(String s, String t) {
        
        // bruteforce soln
        
        /*
        Not Passing Following Test Case
        
        
        "leeeeetcode"
""
        
        
        
        
        
        
        
        */
        
        
        
        
        
        
        //edge cases
        if(s.length()==0)
            return true;
        if(t.length()==0)
            return false;
        int i=0,j=0;
        for(i=0;i<t.length();i++){
            if(s.charAt(j)==t.charAt(i))
                j++;
        }
        if(j==s.length())
            return true;
        return false;
        
    }
}
